mapper.select:
  		SELECT count(*)
		FROM PAGOPA_PAGAMENTI PPA
		WHERE ppa.CODIMP = #{codiceimpresa}
  		AND
  			ppa.CODICEGARA in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.IUV in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.IUV IS NOT NULL
  		
  		AND
  			ppa.IDDEBITO in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.STATOTIP in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.CAUSALETIP in 
  			 
			   #{item}
			 
  		
  		AND
  			 ppa.DATASCADENZA >= #{dataScadenzaDa} 
  		
  		AND
  			 ppa.DATASCADENZA < #{dataScadenzaA}

  		
  		WHERE ppa.CODIMP = #{codiceimpresa}
  		AND
  			ppa.CODICEGARA in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.IUV in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.IUV IS NOT NULL
  		
  		AND
  			ppa.IDDEBITO in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.STATOTIP in 
  			 
			   #{item}
			 
  		
  		AND
  			ppa.CAUSALETIP in 
  			 
			   #{item}
			 
  		
  		AND
  			 ppa.DATASCADENZA >= #{dataScadenzaDa} 
  		
  		AND
  			 ppa.DATASCADENZA < #{dataScadenzaA}

SELECT PPAR.* 
FROM PAGOPA_RICEVUTE PPAR
WHERE PPAR.PAGOPAPAGAMENTIID = #{id}

SELECT COUNT(*)
FROM PAGOPA_PAGAMENTI PPA
WHERE PPA.ID=#{id}

  		SELECT prp.codice,prp.oggetto,prp.sa,prp.cig,prp.usernome
  		FROM v_ws_pagopa_rif_proc prp
  		WHERE (prp.usernome is null or prp.usernome = #{usernome})
		AND
			prp.codice = #{codice}
		
		AND
			prp.oggetto = #{oggetto}
		
		AND
			prp.sa = #{sa}
		
		AND
			prp.cig = #{cig}

mapper.update:
UPDATE pagopa_pagamenti SET
codicegara = #{codicegara}, codimp = #{codimp}, username = #{username}, dcreazione = #{dcreazione}, causalecod = #{causalecod},causaletip = #{causaletip}, servizio = #{servizio}, importo = #{importo}, iuv = #{iuv}, datageniuv = #{datageniuv}, iddebito = #{iddebito}, datascadenza = #{datascadenza}, datainiziovalidita = #{datainiziovalidita}, datafinevalidita = #{datafinevalidita}, datapagamento = #{datapagamento}, idrata = #{idrata}, statocod = #{statocod},statotip = #{statotip}, errore = #{errore},
WHERE ID=#{id}
